#include <signal.h>
#include <unistd.h>
#include "libft.h"

char	*ft_strjoin_4(char *s1, char s2)
{
	char			*res;
	unsigned int	len;
	unsigned int	x;
	unsigned int	i;

	i = 0;
	x = 0;
	if (!s1)
		len = 2;
	else
		len = ft_strlen((char *)s1 + 1);
	res = (char *)malloc(sizeof(char) * (len + 1));
	if (!(res))
		return (NULL);
	while (s1 && s1[x])
	{
		res[x] = s1[x];
		x++;
	}
	res[x + i] = s2;
	i++;
	res[x + i] = '\0';
	if (!s1)
		free(s1);
	return (res);
}

void	ft_print_msg(int signo)
{
	static int	bit = 0;
	static char	tmp = 0;

	tmp |= (signo << bit);
	if (++bit == 8)
	{
		if (tmp == '\0')
			write(1, "\n", 2);
		else
			write(1, &tmp, 1);
		bit = 0;
		tmp = 0;
	}
}
//0123456789
//0123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789

void	ft_handler(int signo)
{
	ft_print_msg(signo == SIGUSR2);
}

int	main(int ac, char **av)
{
	pid_t	pid;

	(void)av;
	if (ac != 1)
		return (-1);
	else
	{
		pid = getpid();
		ft_printf("%d\n", pid);
		signaction(SIGUSR1, ft_handler);
		signaction(SIGUSR2, ft_handler);
		while (1)
			pause();
	}
}
